         schemas[0].Write(writer);

         // Write out the advertisements.
         XmlSerializer xml = new XmlSerializer(typeof(ProductAdvertisement));
         xml.Serialize(writer, x);

         // Close the document.
         writer.WriteEndDocument();
         writer.Close();
     }
 }

