        System.Data.ParameterDirection.Output, true, 0, 0, null,    
        System.Data.DataRowVersion.Default, null);

   cmd.ExecuteNonQuery();

   // Using TdTimestampWithTimeZone to get the time that the student enrolled 
   // in the class
